StructType schema = DataTypes.createStructType(new StructField[] {
DataTypes.createStructField("timestamp", DataTypes.StringType, false),
DataTypes.createStructField("id", DataTypes.StringType, false),
DataTypes.createStructField("value", DataTypes.DoubleType, false)
});
 
Dataset<Row> df = spark.createDataFrame(data, schema);
 
// Show the input data
System.out.println("Input data:");
df.show();
 
// Perform the aggregation
Dataset<Row> result = df.groupBy("id")
.agg(
avg("value").as(METADATA_COL_METRICVALUE),
sum("value").as(METADATA_COL_SUM_VALUE)
);
 
// Show the results
System.out.println("Aggregation results:");
result.show();
 
// Collect the results
List<Row> results = result.collectAsList();
 
// Print the results
System.out.println("Number of results: " + results.size());
for (Row row : results) {
System.out.println("Metric value: " + row.getDouble(row.fieldIndex(METADATA_COL_METRICVALUE)));
System.out.println("Sum value: " + row.getDouble(row.fieldIndex(METADATA_COL_SUM_VALUE)));
}
 
// Verify the results
